}
::-moz-selection {
    background: var(--yellow);
}
a.a--fancy {
    color: var(--brand-color);
    -webkit-transition: color .3s, -webkit-filter .3s;
    transition: color .3s, -webkit-filter .3s;
    -o-transition: color .3s, filter .3s;
    transition: color .3s, filter .3s;
