            animation: colorRotate 2s infinite linear both;
}
@-webkit-keyframes colorRotate {
0% {
        -webkit-filter: hue-rotate(0deg);
                filter: hue-rotate(0deg);
}
100% {
        -webkit-filter: hue-rotate(360deg);
                filter: hue-rotate(360deg);
}
}
@keyframes colorRotate {
0% {
        -webkit-filter: hue-rotate(0deg);
                filter: hue-rotate(0deg);
}
100% {
        -webkit-filter: hue-rotate(360deg);
                filter: hue-rotate(360deg);
}
}
a.a--underline {
    text-shadow: -1px -1px 0 #FFF, 1px -1px 0 #FFF, -1px 1px 0 #FFF, 1px 1px 0 #FFF;
    background: -o-linear-gradient(currentColor, currentColor) 0% 1.05em / 100% .065em no-repeat;
    background: -webkit-gradient(linear, left top, left bottom, from(currentColor), to(currentColor)) 0% 1.05em / 100% .065em no-repeat;
    background: linear-gradient(currentColor, currentColor) 0% 1.05em / 100% .065em no-repeat;
}
b,
strong {
    font-weight: bold;
}
img {
    max-height: 100%;
    -ms-flex-negative: 0;
        flex-shrink: 0;
}
html {
    font-family: 'Zilla slab', sans-serif;
    font-size: calc( 15px + (21 - 15) * (100vw - 400px) / (1800 - 400));
    line-height: 1.6;
}
body {
    background: #fff;
    scroll-behavior: smooth;
}
header {
    padding: 2em;
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
    /* background-color: var(--brand-color); */
