    padding-right: 0;
}
.block {
    display: block;
}
.flex {
    display: -webkit-box;
    display: -ms-flexbox;
    display: flex;
}
.flex--v-center {
    -webkit-box-align: center;
        -ms-flex-align: center;
            align-items: center;
}
.grey-background {
    background-color: var(--gray);
}
.brand-background {
    background-color: var(--brand-color);
}
.black-background {
    background: #000;
    color: #fff;
}
.horizontal-bar {
    -ms-flex-item-align: stretch;
        align-self: stretch;
    background: #000;
    width: .3em;
    margin: 0 1em;
}
.no-margin {
    margin: 0 !important;
}
.margin-top {
    margin-top: 1em;
}
.padded-wrapper {
    padding: 2em;
    width: 100%;
}
td.number-cell,
th.number-cell {
    text-align: right;
}
